import React from 'react';
import './App.css';


class App extends React.Component {
  constructor(){
    super();

    this.state = {
        artistInput: '',
        songInput: ''
    }
  }

  inputUpdate = (e) => {
    this.setState({
      artistInput: e.target.value
    })
  }

  componentDidMount(){
      fetch('https://api.lyrics.ovh/v1/Coldplay/yellow')
      .then(res => res.json())
      .then(data => this.setState({songInput: data}))
  }

  render(){
      console.log(this.state)
      return(
          <div>
              <input type='text' onChange={this.inputUpdate} />
          </div>
      )
  }
}

export default App;
