import React from 'react';
import './App.css';


class App extends React.Component {
  constructor(){
    super();

    this.state = {
        artistInput: '',
        songInput: '',
        lyrics: ''
    }
  }

  lyricsButton = () => {
    this.componentDidMount()
  }


  componentDidMount(){
    const {artistInput, songInput} = this.state
    fetch(`https://api.lyrics.ovh/v1/${artistInput}/${songInput}`)
    .then(res => res.json())
    .then(data => this.setState({lyrics: data}))
  }

  render(){
      console.log(this.state)
      return(
          <div className='App'>
            <input type='text' onChange={(e) => this.setState({artistInput: e.target.value})} />
            <input type='text' onChange={(e) => this.setState({songInput: e.target.value})} />
            <button type='submit' onClick={this.lyricsButton}>Find Lyrics</button>
            <div>
              {this.state.lyrics.lyrics}
            </div>    
          </div>
      )
  }
}

export default App;
