import React from 'react';
import './App.css';


class App extends React.Component {
  constructor(){
    super();

    this.state = {
        artistInput: '',
        songInput: ''
    }
  }


  componentDidMount(){
      fetch('https://api.lyrics.ovh/v1/Coldplay/yellow')
      .then(res => res.json())
      .then(data => this.setState({songInput: data}))
  }

  render(){
      console.log(this.state)
      return(
          <div>
              <input type='text' onChange={(e) => this.setState({artistInput: e.target.value})} />
              <input type='text' onChange={(e) => this.setState({songInput: e.target.value})} />
          </div>
      )
  }
}

export default App;
