import React from 'react';
import './App.css';

import Search from './components/search/search';

class App extends React.Component {
  constructor(){
    super();

    this.state = {
        artistInput: '',
        songInput: ''
    }
  }



  componentDidMount(){
      fetch('https://api.lyrics.ovh/v1/Coldplay/yellow')
      .then(res => res.json())
      .then(data => this.setState({songInput: data}))
  }

  render(){
      console.log(this.state)
      return(
          <div>
              <Search />
          </div>
      )
  }
}

export default App;
